import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";

const Search = () => {
  const [data, setData] = useState([]);
  const [id, setId] = useState("");

  /*Single fetch*/
  const fetchByIdData = () => {
    let a = [];
    axios
      .get(`https://fakerestapi.azurewebsites.net/api/Authors/${id}`)
      .then(result => {
        console.log();
        a.push(result.data);
        console.log(a);

        setData(a);
      })
      .catch(err => {
        console.log("invalid data");
      });
  };

  /*Multi fetch*/

  const fetchAllData = () => {
    axios
      .get("https://fakerestapi.azurewebsites.net/api/Authors")
      .then(result => {
        console.log(result.data);

        setData(result.data);
      })
      .catch(err => {
        console.log(err);
      });
  };

  useEffect(() => {
    if (id) fetchByIdData();
    else fetchAllData();
  }, [id]);

  function settingId(id) {
    setId(id);
  }

  function debounce(f, d) {
    let timer;
    return function() {
      let context = this;
      clearTimeout(timer);
      timer = setTimeout(() => {
        settingId.apply(context, arguments);
      }, d);
    };
  }

  const fun = debounce(settingId, 300);

  return (
    <Fragment>
      <section className="main">
        <form name="login" className="search-form">
          <input
            type="text"
            placeholder="Search"
            name="username"
            id="username"
            required
            value={id}
            autoComplete="off"
            onChange={e => settingId(e.target.value)}
          />
          {/* <div className="button-submit">
            <button type="submit" id="loginButton">
              Search
            </button>
          </div> */}
        </form>
        <div className="table-search">
          <table>
            <tbody>
              <tr>
                <th>Id</th>
                <th>Book id</th>
                <th>First name</th>
                <th>Last name</th>
              </tr>
            </tbody>
            <tbody>
              {data.map(data => (
                <tr key={data.ID}>
                  <td>{data.ID}</td>

                  <td>{data.IDBook}</td>
                  <td>{data.FirstName}</td>
                  <td>{data.LastName}</td>
                  <td>
                    <button
                      type="submit"
                      id="loginButton"
                      className="delete-table"
                    >
                      Delete
                    </button>{" "}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>
    </Fragment>
  );
};

export default Search;
