import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";

const Search = () => {
  const [data, setData] = useState([]);

  /*Single fetch*/
  //   const fetchByIdData = () => {
  //     let a = [];
  //     axios
  //       .get("https://fakerestapi.azurewebsites.net/api/Authors/123")
  //       .then(result => {
  //         console.log(result.data);
  //         a.push(result.data);

  //         setData(a);
  //       })
  //       .catch(err => {
  //         console.log(err);
  //       });
  //   };

  const fetchAllData = () => {
    axios
      .get("https://fakerestapi.azurewebsites.net/api/Authors")
      .then(result => {
        console.log(result.data);

        setData(result.data);
      })
      .catch(err => {
        console.log(err);
      });
  };

  useEffect(() => {
    fetchAllData();
  }, []);
  return (
    <Fragment>
      <section className="main">
        <form name="login" className="search-form">
          <input
            type="text"
            placeholder="Search"
            name="username"
            id="username"
            required
            autoComplete="off"
          />
          {/* <div className="button-submit">
            <button type="submit" id="loginButton">
              Search
            </button>
          </div> */}
        </form>
        <div className="table-search">
          <table>
            <tbody>
              <tr>
                <th>Id</th>
                <th>Book id</th>
                <th>First name</th>
                <th>Last name</th>
              </tr>
            </tbody>
            <tbody>
              {data !== null
                ? data.map(data => (
                    <tr key={data.ID}>
                      <td>{data.ID}</td>

                      <td>{data.IDBook}</td>
                      <td>{data.FirstName}</td>
                      <td>{data.LastName}</td>
                      <td>
                        <button
                          type="submit"
                          id="loginButton"
                          className="delete-table"
                        >
                          Delete
                        </button>{" "}
                      </td>
                    </tr>
                  ))
                : alert()}
            </tbody>
          </table>
        </div>
      </section>
    </Fragment>
  );
};

export default Search;
